    code: "/* 数据 */\n// 矩形树图\nconst bmName = ['消防指挥', '信息技术', '会计学', '外国语言与外国历史']; // 部门名称\nconst bmValue = [100, 50, 80, 60]; // 部门人数\n// 饼图\nconst gwName = [\n  '行政岗',\n  '专任教师岗',\n  '科学研究岗',\n  '双肩挑岗位',\n  '其他',\n  '行政岗',\n  '专任教师岗',\n  '其他',\n  '科学研究岗',\n  '其他',\n  '其他'\n]; // 岗位名称\nconst gwValue = [209, 180, 169, 136, 125, 150, 50, 50, 150, 50, 60]; // 岗位人数\nconst gwPercent = [35, 25, 20, 12, 8, 60, 20, 20, 75, 25, 100]; // 岗位人数占比\nconst gwNum = [5, 3, 2, 1]; // 部门对应岗位数\n\n/* 整合 */\n// 颜色系列\nconst colorSelectList = [\n  'rgba(115, 160, 251, 1)',\n  'rgba(249, 202, 65, 1)',\n  'rgba(115, 222, 180, 1)',\n  'rgba(249, 137, 115, 1)'\n];\nconst colorTreemap = [\n  'rgba(115, 160, 251, 0.25)',\n  'rgba(249, 202, 65, 0.25)',\n  'rgba(115, 222, 180, 0.25)',\n  'rgba(249, 137, 115, 0.25)'\n];\nconst colorPie = ['#73A0FB', '#73DEB4', '#2F467A', '#FACA42', '#F98973'];\n// 矩形树图\nlet treeMapData = [];\nbmName.map((item, index) => {\n  treeMapData.push({\n    name: item,\n    value: bmValue[index],\n    itemStyle: {\n      color: colorTreemap[index]\n    },\n    select: {\n      itemStyle: {\n        color: colorSelectList[index]\n      }\n    }\n  });\n});\n// 饼图\nlet pieData = {};\nlet currentIndex = 0; // 当前岗位下标\nbmName.map((item, index) => {\n  let list = [];\n  for (let i = currentIndex; i < currentIndex + gwNum[index]; i++) {\n    list.push({\n      name: gwName[i],\n      value: gwValue[i],\n      percent: gwPercent[i]\n    });\n  }\n  currentIndex += gwNum[index];\n  pieData[item] = list;\n});\n\n/* 函数 */\n//获取当前部门的饼图数据\nconst getPieData = (bm) => {\n  return pieData[bm];\n};\n// 获取当前岗位的legend数据\nconst getLegendData = (bm) => {\n  let pieData = getPieData(bm);\n  return pieData.map((item) => item.name);\n};\n\n/* 当前数据 */\n// 饼图当前数据\nlet currentPieData = getPieData(bmName[0]);\n// 当前部门\nlet currentBm = bmName[0];\n\n/* series数据 */\nlet seriesData = [];\n// 树图series\nseriesData.push({\n  name: 'treemap',\n  id: 'treemap',\n  type: 'treemap',\n  data: treeMapData,\n  roam: false, //拖拽漫游（移动和缩放）\n  nodeClick: false, // 节点点击\n  breadcrumb: false, // 面包屑\n  width: '45%',\n  left: '16',\n  top: 300,\n  bottom: 300,\n  label: {\n    show: true,\n    position: [8, 8],\n    formatter: function (params) {\n      return `{name|${params.name}}\\n{value|${params.value}人}`;\n    },\n    rich: {\n      name: {\n        fontSize: 12,\n        fontFamily: 'Source Han Sans CN-Regular',\n        color: '#fff'\n      },\n      value: {\n        fontSize: 24,\n        fontFamily: 'HarmonyOS Sans-Medium',\n        color: '#fff',\n        padding: [10, 0, 0, 0]\n      }\n    }\n  },\n  selectedMode: 'single',\n  itemStyle: {\n    borderWidth: 2,\n    borderColor: '#fff'\n  },\n  tooltip: {\n    trigger: 'item',\n    axisPointer: {\n      type: 'none'\n    },\n    confine: true,\n    formatter: (params) => {\n      return `\n\t\t\t\t   <div style=\"font-size: 14px;font-family: Source Han Sans CN-Medium;font-weight: 500;color: #FFFFFF;margin-bottom:8px;\">${params.name}</div>\n               <div style=\"font-size: 14px;font-family: Source Han Sans CN-Medium;font-weight: 500;color: #FFFFFF;\">部门在岗在编人数：${params.value}人</div>\n\t\t\t    `;\n    },\n    extraCssText:\n      'opacity: 0.8;background-color:#050F1B;padding:12px;box-shadow: 1px 6px 15px 1px rgba(0,0,0,0.13);border-radius: 4px;filter: blur(undefinedpx);border:none;'\n  }\n});\n// 饼图series\nseriesData.push({\n  name: 'pie',\n  id: 'pie',\n  type: 'pie',\n  left: '40%',\n  top: 'center',\n  width: '45%',\n  color: colorPie,\n  label: { show: false },\n  radius: ['30%', '40%'],\n  data: currentPieData\n});\n\noption = {\n  graphic: {\n    type: 'image',\n    top: 'center',\n    left: '48%',\n    style: {\n      image:\n        'data:image/png;base64,'\n    }\n  },\n  title: {\n    text: `{bm| ${currentBm}}{txt|在岗在编岗位系列人员分布}`,\n    textStyle: {\n      rich: {\n        bm: {\n          color: 'rgba(151, 30, 35, 1)',\n          fontFamily: 'Source Han Sans CN-Regular',\n          fontSize: 14\n        },\n        txt: {\n          color: 'rgba(0, 0, 0, 0.85)',\n          fontFamily: 'Source Han Sans CN-Regular',\n          fontSize: 14,\n          padding: [0, 0, 0, 8]\n        }\n      }\n    },\n    backgroundColor: {\n      type: 'linear',\n      x: 0,\n      y: 0,\n      x2: 1,\n      y2: 0,\n      colorStops: [\n        {\n          offset: 0,\n          color: 'rgba(151, 30, 35, 0.2)' // 0% 处的颜色\n        },\n        {\n          offset: 1,\n          color: 'rgba(151, 30, 35, 0)' // 100% 处的颜色\n        }\n      ],\n      global: false // 缺省为 false\n    },\n    left: '52%',\n    top: '20%'\n  },\n  tooltip: {},\n  legend: {\n    type: 'scroll',\n    orient: 'vertical',\n    //height: '80%',\n    right: '16',\n    top: 'center',\n    icon: 'circle', //设置为圆，删除则为矩形\n    itemWidth: 6,\n    itemHeight: 6,\n    itemGap: 25,\n    data: getLegendData(bmName[0]),\n    formatter: function (params) {\n      for (let i = 0; i < currentPieData.length; i++) {\n        if (params === currentPieData[i].name) {\n          return (\n            '{name|' +\n            params +\n            '}{value|' +\n            currentPieData[i].value +\n            '人}{rate|' +\n            currentPieData[i].percent +\n            '%}'\n          );\n        }\n      }\n    },\n    textStyle: {\n      rich: {\n        name: {\n          fontSize: 14,\n          fontWeight: 400,\n          width: 80,\n          //height: 20,\n          padding: [0, 0, 0, 5],\n          color: 'rgba(0, 0, 0, 0.65)',\n          fontFamily: 'Source Han Sans CN-Regular'\n        },\n        rate: {\n          fontSize: 14,\n          fontWeight: 500,\n          //height: 20,\n          width: 50,\n          align: 'right',\n          color: 'rgba(0, 0, 0, 0.85)',\n          fontFamily: 'Source Han Sans CN-Medium'\n        },\n        value: {\n          fontSize: 14,\n          fontWeight: 500,\n          //height: 20,\n          width: 50,\n          align: 'right',\n          color: 'rgba(0, 0, 0, 0.85)',\n          fontFamily: 'Source Han Sans CN-Medium'\n        }\n      }\n    }\n  },\n  series: seriesData\n};\n// 设置默认选中\nsetTimeout(() => {\n  myChart.dispatchAction({\n    type: 'select',\n    seriesName: 'treemap',\n    name: bmName[0]\n  });\n}, 50);\n\n// 点击事件\nmyChart.on('click', 'series.treemap', (param) => {\n  let seriesName = param.seriesName; // seriesName\n  let index = param.dataIndex - 1;\n  currentPieData = getPieData(bmName[index]);\n  currentBm = bmName[index];\n  myChart.setOption({\n    title: {\n      text: `{bm| ${currentBm}}{txt|在岗在编岗位系列人员分布}`\n    },\n    series: [\n      {\n        id: 'pie',\n        data: currentPieData\n      }\n    ]\n  });\n  myChart.dispatchAction({\n    type: 'select',\n    seriesName: 'treemap',\n    name: bmName[index]\n  });\n});"
